import React, { Component } from "react";
import SpotsManager from "../../modules/SpotsManager";
import "./GigsForm.css";

class SpotsForm extends Component {
  state = {
    spot: "",
    loadingStatus: false
  };

  handleFieldChange = evt => {
    const stateToChange = {};
    stateToChange[evt.target.id] = evt.target.value;
    this.setState(stateToChange);
  };

  /*  Local method for validation, set loadingStatus, create spot object, invoke the SpotssManager post method, and redirect to the full spot list
   */
  constructNewSpot = evt => {
    evt.preventDefault();
    if (
      this.state.spot === ""
    ) {
      window.alert("Please input a spot type");
    } else {
      this.setState({ loadingStatus: true });
      const spot = {
        spot: this.state.name,
        userId: parseInt(sessionStorage.getItem("credentials")),
      };

      // Create the spot and redirect user to spot list
      SpotsManager.post(spot).then(() => this.props.history.push("/gigs"));    
    }
  };

  render() {
    return (
      <>
        <form>
          <fieldset>
            <div className="formgrid">
              <input
                type="text"
                required
                onChange={this.handleFieldChange}
                id="spot"
                placeholder="In What Spot Did You Perform"
              />
              <label htmlFor="spot">Spot</label>
            </div>

            <div className="alignRight">
              <button
                type="button"
                disabled={this.state.loadingStatus}
                onClick={this.constructNewSpot}
              >
                Submit
              </button>
            </div>
          </fieldset>
        </form>
      </>
    );
  }
}

export default SpotsForm;
