import React, { Component } from 'react';
import './Gigs.css'
import { Link } from "react-router-dom";

class GigsCard extends Component {
  render() {
    return (
      <div className="card">
          <h3><span className="gigName" style={{ color: 'darkslategrey' }}> {this.props.gig.name}</span></h3>
          <p className="gigDate">Date: {this.props.gig.date}</p>
          {/* <p className="venueName">Name Of Venue: {this.props.gig.venue.name}</p>
          <p className="venueCity">City: {this.props.gig.venue.city}</p>
          <p className="venueState">State: {this.props.gig.venue.state}</p>
          <p className="venueCapacity">Venue Capacity: {this.props.gig.venue.capacity}</p>
          <p className="audienceName">Type Of Audience: {this.props.gig.audience.name}</p> 
          <p className="spotName">Spot: {this.props.gig.spot.name}</p> */}
          <picture>
            <img src={require('./charlieChaplin.png')} alt="Chaplin" />
          </picture>
          <button type="button" onClick={() => this.props.deleteGig(this.props.gig.id)}>Delete Gig</button>
          <button type="button" onClick={() => {this.props.history.push(`/gigs/${this.props.gig.id}/edit`)}}>Edit</button>
          <Link to={`/gigs/${this.props.gig.id}`}><button>Details</button></Link>
          </div>
    );
  } 
}

export default GigsCard; 

