import React from "react"
import { Link } from "react-router-dom"
import VenuesEditForm from "../Venues/VenuesEditForm"
import AudiencesEditForm from "../Audiences/AudiencesEditForm"

/*
    Now using object destructuring to pull out the individual
    keys in the `props` object that gets passed to this
    component.

    https://tylermcginnis.com/videos/object-array-destructuring/
*/
const DetailCard2 = ({id, name, date, resource, rating, city, state, capacity}) => {
    const {gigId, gigName, gigDate, gigResource, gigRating} = getGigs()
    const {venueId, venueName, venueResource, venueCity, venueState, venueCapacity} = getVenues()
    const {audienceId, audienceName} = getAudiences()
    const {spotId, spotName} = getSpots()

    return (
        <div className="card" style={{width: `18rem`}}>
            <div className="card-body">
                <h5 className="card-title">
                    {name}
                </h5>
                <p className="card-text">{addlInfo ? addlInfo : ""}</p>
                {
                    <Link className="card-link"
                        to={{
                            pathname: `/${resource}/${id}`,
                            state: {
                                id: id,
                                name: name,
                                date: date,
                                resource: resource,
                                addlInfo: addlInfo
                            }
                        }}>
                        Details
                    </Link> 
                }
            </div>
        </div>
    )
}

export default DetailCard2
