import React, { Component } from 'react';
import GigsManager from '../../modules/GigsManager';
import AudiencesManager from '../../modules/AudiencesManager'
import VenuesManager from '../../modules/VenuesManager'
// import SpotsManager from '../../modules/SpotsManager'

class AllDetails extends Component {

  state = {
      gig: "",
      date: "",
      venue: "",
    //   spot: "",
      audience: "",
      rating: 0,
      loadingStatus: true, 
  }

  componentDidMount(){
    console.log("GigsDetail: ComponentDidMount");
    //get(id) from GigsManager and hang on to the data; put it into state
    GigsManager.get(this.props.gigId)
    .then((gig) => {
      this.setState({
        gig: gig.gig,
        loadingStatus: false,
      });
    });
  }

  componentDidMount(){
    console.log("AudiencesDetail: ComponentDidMount");
    //get(id) from AudiencesManager and hang on to the data; put it into state
    AudiencesManager.get(this.props.audienceId)
    .then((audience) => {
      this.setState({
        audience: audience.audience,
        loadingStatus: false,
      });
    });
  }

  componentDidMount(){
    console.log("VenuesDetail: ComponentDidMount");
    //get(id) from VenuesManager and hang on to the data; put it into state
    VenuesManager.get(this.props.venueId)
    .then((venue) => {
      this.setState({
        venue: venue.venue,
        loadingStatus: false,
      });
    });
  }

//   componentDidMount(){
//     console.log("SpotsDetail: ComponentDidMount");
//     //get(id) from SpotsManager and hang on to the data; put it into state
//     SpotsManager.get(this.props.spotId)
//     .then((spot) => {
//       this.setState({
//         spot: spot.spot,
//         loadingStatus: false,
//       });
//     });
//   }

  handleDelete = () => {
    //invoke the delete function in GigsManager and re-direct to the gig list.
    this.setState({loadingStatus: true})
    GigsManager.delete(this.props.gigId)
    .then(() => this.props.history.push("/gigs?expand=spots"))
}

  render() {
    return (
        <>
      <div className="card">
          {this.props.gig.gig} {this.props.gig.date} {this.props.venue.venue} {this.props.audience.audience} {this.props.gig.rating} 
        <button type="button" onClick={() => { this.props.history.push(`/gigs${this.props.gig.id}/edit`) (`/venues${this.props.venue.id}/edit`) }}>Edit</button>
        <button type="button" disabled={this.state.loadingStatus} onClick={this.handleDelete}>Delete Gig</button>
      </div>
        </>

    );
  }
}

export default AllDetails;
