import React, { Component } from "react"
import AudiencesManager from "../../modules/AudiencesManager"
import "./AudiencesForm.css"

class AudiencesEditForm extends Component {
    //set the initial state
    state = {
      audience: "",
      loadingStatus: true,
    };
    constructor() {
        super();

        this.state = {
            showMenu: false,
        };

        this.showMenu = this.showMenu.bind(this);
        this.closeMenu = this.closeMenu.bind(this);
    }

    showMenu(editedAudience) {
        editedAudience.preventDefault();

        this.setState({ showMenu: true }, () => {
            document.addEventListener('click', this.closeMenu);
        });
    }

    closeMenu(editedAudience) {

        if (!this.dropdownMenu===(editedAudience.target)) {

            this.setState({ showMenu: false }, () => {
                document.removeEventListener('click', this.closeMenu);
            });  

        }
        AudiencesManager.update(editedAudience)
      .then(() => this.props.history.push("/audiences"))
    }

    render() {
        return (
            <div>
          <button onClick={this.showMenu}>
            How Was The Crowd?
          </button>

          {
              this.state.showMenu
              ? (
                  <div
                  className="menu"
                  ref={(element) => {
                      this.dropdownMenu = element;
                    }}
                    >
                  <button> Hot </button>
                  <button> Cold </button>
                  <button> Corporate </button>
                  <button> Sparse </button>
                  <button> Engaged </button>
                  <button> A Buncha Drunks </button>
                  <button> A Buncha Hecklers </button>
                </div>
              )
              : (
                  null
                  )
                }
        </div>
      );
    }
}

export default AudiencesEditForm
