import React, { Component } from "react";
import GigsManager from "../../modules/GigsManager";
import VenuesManager from "../../modules/VenuesManager";
import AudiencesManager from "../../modules/AudiencesManager";
import SpotsManager from "../../modules/SpotsManager";
import "./GigsForm.css";

class GigsForm extends Component {
  state = {
    gig: "",
    date: "",
    rating: 0,
    loadingStatus: false
  };

  handleFieldChange = evt => {
    const stateToChange = {};
    stateToChange[evt.target.id] = evt.target.value;
    this.setState(stateToChange);
  };

  /*  Local method for validation, set loadingStatus, create gig object, invoke the GigsManager post method, and redirect to the full gig list
   */
  constructNewGig = evt => {
    evt.preventDefault();
    if (
      this.state.gig === "" ||
      this.state.date === "" ||
      this.state.rating === ""
    ) {
      window.alert("Please input a gig and date");
    } else {
      this.setState({ loadingStatus: true });
      const gig = {
        gig: this.state.gig,
        date: this.state.date,
        rating: this.state.rating,
        userId: parseInt(sessionStorage.getItem("credentials"))
        // venueId: parseInt(sessionStorage.getItem("credentials"))
      };

      // Create the gig and redirect user to gig list
      GigsManager.post(gig).then(() => this.props.history.push("/gigs"));
      VenuesManager.post(gig).then(() => this.props.history.push("/venues")); 
      AudiencesManager.post(gig).then(() => this.props.history.push("/audiences"));
      SpotsManager.post(gig).then(() => this.props.history.push("/spots"))

    }
  };

  render() {
    return (
      <>
        <form>
          <fieldset>
            <div className="formgrid">
              <input
                type="text"
                required
                onChange={this.handleFieldChange}
                id="gig"
                placeholder="Gig"
              />
              <label htmlFor="gig">Gig</label>

              <input
                type="date"
                required
                onChange={this.handleFieldChange}
                id="date"
                placeholder="Date Of Gig"
              />
              <label htmlFor="date">Date</label>

              <input
                type="text"
                required
                onChange={this.handleFieldChange}
                id="spot"
                placeholder="In What Spot Did You Perform"
              />
              <label htmlFor="spot">Spot</label>

              <input
                type="text"
                required
                onChange={this.handleFieldChange}
                id="rating"
                placeholder="Rate Your Set"
              />
              <label htmlFor="rating">Rating</label>
            </div>

            <div className="formgrid">
              <input
                type="text"
                required
                onChange={this.handleFieldChange}
                id="venue"
                placeholder="Name Of Venue"
              />
              <label htmlFor="venue">Venue</label>

              <input
                type="text"
                required
                onChange={this.handleFieldChange}
                id="city"
                placeholder="City"
              />
              <label htmlFor="city">City</label>

              <input
                type="text"
                required
                onChange={this.handleFieldChange}
                id="state"
                placeholder="State"
              />
              <label htmlFor="state">State</label>

              <input
                type="text"
                required
                onChange={this.handleFieldChange}
                id="capacity"
                placeholder="Capacity"
              />
              <label htmlFor="capacity">Capacity</label>
            </div>

            <div className="alignRight">
              <button
                type="button"
                disabled={this.state.loadingStatus}
                onClick={this.constructNewGig}
              >
                Submit
              </button>
            </div>
          </fieldset>
        </form>
      </>
    );
  }
}

export default GigsForm;
