import React, { Component } from "react"
import DetailCard from "./DetailCard";


export default class GenericAudiencesList extends Component {
    state = { audiences: [] }

    componentDidMount () {
        fetch("http://localhost:8088/audiences")
        .then(e => e.json())
        .then(audiences => this.setState({ audiences: audiences }))
    }

    render() {
        return (
            <React.Fragment>
                {
                    this.state.audiences.map(audience =>
                        <DetailCard id={audience.id}
                                    name={audience.name}
                                    resource="audiences" />
                    )
                }
            </React.Fragment>
        )
    } 
}
