import React from "react"
import { Link } from "react-router-dom"
/*
    Now using object destructuring to pull out the individual
    keys in the `props` object that gets passed to this
    component.

    https://tylermcginnis.com/videos/object-array-destructuring/
*/
const DetailCard = ({id, gigName, resource, date, rating, venueId, venueName, venueCity, venueCapacity, venueState, audienceName, audienceId, spotName, spotId}) => {
    return (
        <>
        <div className="card" style={{width: `18rem`}}>
            <div className="card-body">
                <h5 className="card-title">
                    {gigName}
                </h5>
                <p className="card-text-date">Date{date ? date : ""}</p>
                <p className="card-text-rating">Set Rating: {rating ? rating : ""}</p>
                <p className="card-text-venueName">Venue Name: {venueName ? venueName : ""}</p>
                <p className="card-text-venueCity">City: {venueCity ? venueCity : ""}</p>
                <p className="card-text-venueState">State: {venueState ? venueState : ""}</p>
                <p className="card-text-venueCapacity">Seating Capacity: {venueCapacity ? venueCapacity : ""}</p>
                <p className="card-text-audienceName">Type Of Audience: {audienceName ? audienceName : ""}</p>
                <p className="card-text-spotName">Spot Order: {spotName ? spotName : ""}</p>
                {
                    <Link className="card-link"
                        to={{
                            pathname: `http:localhost:8088/${resource}/${id}`,
                            state: {
                                id: id,
                                gigName: gigName,
                                resource: resource,
                                date: date,
                                rating: rating,
                                venueId: venueId,
                                venueName: venueName,
                                venueCity: venueCity,
                                venueState: venueState,
                                audienceName: audienceName,
                                audienceId: audienceId,
                                spotName: spotName,
                                spotId: spotId


                            }
                        }}>
                        Details
                        <button type="button" onClick={() => this.props.deleteGig(this.props.gig.id)}>Delete Gig</button>
                        <button type="button" onClick={() => {this.props.history.push(`/gigs/${this.props.gig.id}/edit`)}}>Edit</button>
                    </Link>
                }
            </div>
        </div>
        </>
    )
}

export default DetailCard 


