import React, { Component } from "react";
import VenuesCard from "../Venues/VenuesCard";
import VenuesManager from '../../modules/VenuesManager';

export default class VenuesList2 extends Component {
  state = { venues: [] };

  loggedInUser = parseInt(sessionStorage.getItem("credentials"));
  componentDidMount() {
    VenuesManager.getAll(this.loggedInUser).then(venues => this.setState({ venues: venues }))

  }

  deleteGig = id => {
    GigsManager.delete(id).then(() => {
      GigsManager.getAll(this.loggedInUser).then(newGigs => {
        this.setState({
          gigs: newGigs
        });
      });
    });
  };

  render() {
    return (
      <React.Fragment>
        <section className="section-content">
          <button
            type="button"
            className="btn"
            onClick={() => {
              this.props.history.push("/venues/new");
            }}
          >
            Add Venue
          </button>
        </section>

        {this.state.venues.map(venue => (

          <VenuesCard id={venue.id} venue={venue} name={venue.name}  resource="venues" />
        ))}
      </React.Fragment>
    );
  }
}
