import React, { Component } from "react";
import DetailCard from "../Presentation/DetailCard";
import GigsManager from "../../modules/GigsManager";

export default class GenericGigsList extends Component {
  state = { gigs: [] };
  loggedInUser = parseInt(sessionStorage.getItem("credentials"));

  componentDidMount() {
    fetch("http://localhost:8088/gigs").then(e => e.json());
    GigsManager.getAll(this.loggedInUser).then(gigs =>
      this.setState({ gigs: gigs })
    );
  }

  deleteGig = id => {
    GigsManager.delete(id).then(() => {
      GigsManager.getAll(this.loggedInUser).then(newGigs => {
        this.setState({
          gigs: newGigs
        });
      });
    });
  };

  render() {
    return (
      <React.Fragment>
        <section className="section-content">
          <button
            type="button"
            className="btn"
            onClick={() => {
              this.props.history.push("/gigs/new");
            }}
          >
            Add Gig
          </button>
        </section>
        <div className="container-cards">
          {this.state.gigs.map(gig => (
            <DetailCard
              key={gig.id}
              gigName={gig.name}
              resource="gigs"
              date={gig.date}
              rating={gig.rating}
              key={gig.venue.id}
              venueName={gig.venue.name}
              venueCity={venue.city}
              venueState={venue.state}
              venueCapacity={venue.capacity}
            />
            ))}
            </div>
        }
      </React.Fragment>
    );
    }}
