import React, { Component } from "react";
import GigsManager from "../../modules/GigsManager";
import "./GigsForm.css";

class GigsEditForm extends Component {
  //set the initial state
  state = {
    gig: "",
    date: "",
    audience: "",
    venue: "",
    spot: "",
    loadingStatus: true
  };

  handleFieldChange = evt => {
    const stateToChange = {};
    stateToChange[evt.target.id] = evt.target.value;
    this.setState(stateToChange);
  };

  updateExistingGig = evt => {
    evt.preventDefault();
    const editedGig = {
      gig: this.state.name,
      date: this.state.date,
      venueId: this.state.match.params.venue.id,
      audienceId: this.props.match.params.audience.id,
      spotId: this.props.match.params.spot.id,
      id: this.props.match.params.id,
      userId: parseInt(sessionStorage.getItem("credentials"))
    };

    GigsManager.update(editedGig).then(() => this.props.history.push("/gigs"));
    this.setState({ loadingStatus: true });
  };

  componentDidMount() {
    GigsManager.get(this.props.match.params.gigId).then(gig => {
      this.setState({
        gig: gig.name,
        date: gig.date,
        venueId: this.state.venue.id,
        audienceId: this.state.audience.id,
        spotId: this.props.match.params.spotId,
        loadingStatus: false
      });
    });
  } 




  render() {  
    return (
      <>
        <form>
          <fieldset>
            <div className="formgrid">
              <input
                type="text"
                required
                className="form-control"
                onChange={this.handleFieldChange}
                id="gig"
                value={this.state.name}
                />
              <label htmlFor="gig">Gig</label>

              <input
                type="date"
                required
                className="form-control"
                onChange={this.handleFieldChange}
                id="date"
                value={this.state.date}
              />
              <label htmlFor="date">Date</label>
              </div>

            {/* <select
            type="select"
            required
              className="form-control"
              id="venueId"
              value={this.state.venueId}
              onChange={this.handleFieldChange}
              >
              {this.state.venues.map(venue => (
                <option key={venue.id} value={this.state.venueId}>
                  {venue.name}
                </option>
              ))}
              <label htmlFor="venue">Venue</label>
              </select> */}

            <div className="alignRight">
              <button
                type="button"
                disabled={this.state.loadingStatus}
                onClick={this.updateExistingGig}
                className="btn btn-primary"
                >
                Submit
              </button>
            </div>
          </fieldset>
        </form>
    </>
    );
  }
}

export default GigsEditForm;
