import React, { Component } from 'react';
import './Gigs.css'
import { Link } from "react-router-dom";

class GigsCard extends Component {
  render() {
    return (
      <div className="card">
          {this.props.gig.name} {this.props.gig.date} 
          <picture>
            <img src={require('./charlieChaplin.png')} alt="Chaplin" />
          </picture>
          <button type="button" onClick={() => this.props.deleteVenue(this.props.venue.id)}>Delete Gig</button>
          <button type="button" onClick={() => {this.props.history.push(`/venues/${this.props.venue.id}/edit`)}}>Edit</button>
          <button type="button" onClick={() => { this.props.history.push(`/venues/${this.props.venue.id}/venues`) }}>Details</button>
          </div>
    );
  }
}

export default VenuesCard;
