import React, { Component } from "react";
import DetailCard from "../Presentation/DetailCard";
import VenuesManager from '../../modules/VenuesManager'


export default class GenericVenuesList extends Component {
    state = { locations: [] }
    loggedInUser = parseInt(sessionStorage.getItem("credentials"));

    componentDidMount () {
        fetch("http://localhost:8088/venues")
        .then(e => e.json())
        VenuesManager.getAll(this.loggedInUser)
        .then(venues => this.setState({ venues: venues }))
    }

    render() {
        return (
            <React.Fragment>
                <ul>
                {
                    this.state.venues.map(venue =>
                        <DetailCard id={venue.id}
                            name={venue.name}
                            resource="venues" 
                            addlInfo={venue.city}
                            addlInfo={venue.state}
                            addlInfo={venue.capacity}/>
                    )
                }
                </ul>
            </React.Fragment>
        )
    }
}
