import React, { Component } from 'react';
import './Gigs.css'


class GigsCard extends Component {
  render() {
    return (
      <div className="gigs-card">
          <h2 className="gigName"> {this.props.gig.name}</h2>
          <h3 className="gigDate">{this.props.gig.date}</h3>
          <h3 className="venueName">{this.props.gig.venue.name}</h3>
          <h3 className="venueCity">{this.props.gig.venue.city}</h3>
          <h3 className="venueState">{this.props.gig.venue.state}</h3>
          <h3 className="venueCapacity">The Venue seats {this.props.gig.venue.capacity} people</h3>
          <h3 className="audienceName">The Audience Was {this.props.gig.audience.name}</h3> 
          <h3 className="spotName">Spot Order: {this.props.gig.spot.name}</h3>
          {/* <picture>
            <img src={require('./charlieChaplin.png')} alt="Chaplin" />
          </picture> */}
          <button type="button" className="addGig--btn" onClick={() => {this.props.history.push("/gigs/new") }}>Add Gig</button>
          <button type="button" className="edit--btn" onClick={() => {this.props.history.push(`/gigs/${this.props.gig.id}/edit`)}}>Edit</button>
          <button type="button" className="delete--btn"  onClick={() => this.props.deleteGig(this.props.gig.id)}>Delete Gig</button>
      </div>
    );
  } 
}

export default GigsCard; 

