import React, { Component } from "react";
import GigsCard from "./GigsCard";
import GigsManager from "../../modules/GigsManager";

export default class GigsList2 extends Component {
  state = { gigs: [] };

  loggedInUser = parseInt(sessionStorage.getItem("credentials"));
  componentDidMount() {
    GigsManager.getAll(this.loggedInUser).then(gigs => this.setState({ gigs: gigs }))

  }

  deleteGig = id => {
        GigsManager.delete(id).then(() => {
          GigsManager.getAll(this.loggedInUser).then(newGigs => {
            this.setState({
              gigs: newGigs
            });
          });
        });
      };

      handleDelete = () => {
            //invoke the delete function in GigsManager and re-direct to the gig list.
            this.setState({loadingStatus: true})
            GigsManager.delete(this.props.gigId)
            .then(() => this.props.history.push("/gigs"))
      }

  render() {
    return (
      <React.Fragment>
        <section className="section-content">
          <button
            type="button"
            className="btn"
            onClick={() => {
              this.props.history.push("/gigs/new");
            }}
          >
            Add Gig
          </button>
        </section>

        {this.state.gigs.map(gig => (

          <GigsCard key={gig.id} valuegig={gig} name={gig.name} date={gig.date} resource="gigs" deleteGig={this.deleteGig} 
          {...this.props}/>
        ))}
      </React.Fragment>
    );
  }
}
