import React, { Component } from "react";
import GigsManager from "../../modules/GigsManager";
import "./GigsForm.css";
import VenuesManager from "../../modules/VenuesManager";

class GigsForm extends Component {
  state = {
    gig: "",
    date: "",
    venue: [],
    audience: [],
    spot: [],
    loadingStatus: false
  };
  componentDidMount() {
    GigsManager.get(this.props.match.params.id).then(gig => {
      this.setState({
        gig: gig.name,
        date: gig.date,
        venue: this.state.venue.id,
        audience: this.state.audience.id,
        spot: this.state.spot.id,
      }); 
      console.log("GigDetail: ComponentDidMount");
    // get(id) from GigsManager and hang on to the data; put it into state
    VenuesManager.get(this.props.venueId)
    .then((venue) => {
      this.setState({
        venue: venue.gig,
        venue: [],
        audience: [],
        spot: [],
        loadingStatus: false,
      });
    });   
    })
  }


  handleFieldChange = evt => {
    const stateToChange = {};
    stateToChange[evt.target.id] = evt.target.value;
    this.setState(stateToChange);
  };

  /*  Local method for validation, set loadingStatus, create gig object, invoke the GigsManager post method, and redirect to the full gig list
   */

  constructNewGig = evt => {
    evt.preventDefault();
    if (this.state.gig === "" || this.state.date === "") {
      window.alert("Please input a gig and date");
    } else {
      this.setState({ loadingStatus: true });
      const gig = {
        gig: this.state.name,
        date: this.state.date,
        venueId: this.venue.id,
        audienceId: this.state.audience.id,
        spotId: this.state.spot.id,
        userId: parseInt(sessionStorage.getItem("credentials"))
      };


      // Create the gig and redirect user to gig list
      GigsManager.post(gig).then(() => this.props.history.push("/gigs"));
    }
  };


  render() {
    return (
      <>
        <form>
          <fieldset>
            <div className="formgrid">
              <input
                type="text"
                required
                onChange={this.handleFieldChange}
                id="gig"
                placeholder="Gig"
              />
              <label htmlFor="gig">Gig</label>

              <input
                type="date"
                required
                onChange={this.handleFieldChange}
                id="date"
                placeholder="Date Of Gig"
              />
              <label htmlFor="date">Date</label>
            </div>

            <select
              className="form-control"
              id="venueId"
              value={this.state.venueId}
              onChange={this.handleFieldChange}
            >
              {this.state.venues.map(venue => (
                <option key={venue.id} value={this.state.venue.id}>
                  {venue.name} {venue.city} {venue.capacity}
                </option>
              ))}
            </select>
            <label htmlFor="venue">Venue</label>

            <div className="alignRight">
              <button
                type="button"
                disabled={this.state.loadingStatus}
                onClick={this.constructNewGig}
              >
                Submit
              </button>
            </div>
          </fieldset>
        </form>
      </>
    );
  }
}

export default GigsForm;
