import React, { Component } from "react";
import DetailCard from "../Presentation/DetailCard";
import GigsManager from "../../modules/GigsManager";
import AudiencesManager from "../../modules/AudiencesManager";
import VenuesManager from "../../modules/VenuesManager";
import SpotsManager from "../../modules/SpotsManager";

const ComponentsManager = {
  GigsManager,
  AudiencesManager,
  VenuesManager,
  SpotsManager
};
const GigsURL = `http://localhost:8088/gigs`;
const VenuesURL = `http://localhost:8088/venues`;
const AudiencesURL = `http://localhost:8088/audiences`;
const SpotsURL = `http://localhost:8088/spots`;

export default class ResourceList extends Component {
  state = { components: [] };
  loggedInUser = parseInt(sessionStorage.getItem("credentials"));

  componentDidMount() {
    fetch(`${GigsURL}`)
    fetch(`${VenuesURL}`)
    fetch(`${AudiencesURL}`)
    fetch(`${SpotsURL}`).then(e => e.json());
    ComponentsManager.getAll(this.loggedInUser)
    .then(id, gigName, resource, date, rating, venueName, venueCity, venueState, audienceName, spotName  =>
      this.setState({ 
        id: id,
        gigName: gigName,
        resource: resource,
        date: date,
        rating: rating,
        venueName: venueName,
        venueCity: venueCity,
        venueState: venueState,
        audienceName: audienceName,
        spotName: spotName,
    })

);
  }

  render() {
    return (
      <React.Fragment>
        {this.state.components.map(componentObj => (
          <DetailCard
            key={componentObj.gigs.id}
            key={componentObj.venues.id}
            key={componentObj.audiences.id}
            key={componentObj.spots.id}
            name={componentObj.gigs.name}
            resource="components"
            addlInfo={componentObj.date}
            addlInfo={componentObj.rating}
            addlInfo={componentObj.city}
            addlInfo={componentObj.state}
          />
        ))}
        <section className="section-content">
          <button
            type="button"
            className="btn"
            onClick={() => {
              this.props.history.push("/gigs/new");
            }}
          >
            Add Gig
          </button>
        </section>
      </React.Fragment>
    );
  }
}
