import React, { Component } from "react";
import DetailCard from "../Presentation/DetailCard";
import VenuesManager from '../../modules/VenuesManager'


export default class GenericVenuesList extends Component {
    state = { venues: [] }
    loggedInUser = parseInt(sessionStorage.getItem("credentials"));

    componentDidMount () {
        fetch("http://localhost:8088/venues")
        .then(e => e.json())
        VenuesManager.getAll(this.loggedInUser)
        .then(venues => this.setState({ venues: venues }))
    }

    render() {
        return (
            <React.Fragment>
                <section className="section-content">
          <button
            type="button"
            className="btn"
            onClick={() => {
              this.props.history.push("/gigs/new");
            }}
          >
            Add Gig
          </button>
        </section>
        <div className="container-cards">
          {this.state.gigs.map(gig => (
            <DetailCard
              key={gig.id}
              name={gig.gig}
              resource="gigs"
              date={gig.date}
              rating={gig.rating}
            />
            ))} 
            </div>
                <ul>
                {
                    this.state.venues.map(venue =>
                        <DetailCard key={venue.id}
                                    venueName={venue.name}
                                    venueCity={venue.city}
                                    venueState={venue.state}
                                    venueCapacity={venue.capacity}
                                    resource="venues"  
                            />
                    )
                }
                </ul>
            </React.Fragment>
        )
    }
}
