import React, { Component } from "react";
import AudiencesCard from "../Audiences/AudiencesCard";
import AudiencesManager from '../../modules/AudiencesManager';

export default class AudiencesList2 extends Component {
  state = { audiences: [] };

  loggedInUser = parseInt(sessionStorage.getItem("credentials"));
  componentDidMount() {
    GigsManager.getAll(this.loggedInUser).then(audiences => this.setState({ audiences: audiences }))

  }

  render() {
    return (
      <React.Fragment>
        <section className="section-content">
          <button
            type="button"
            className="btn"
            onClick={() => {
              this.props.history.push("/audiences/new");
            }}
          >
            Add Audience Type
          </button>
        </section>

        {this.state.gigs.map(gig => (

          <GigsCard id={gig.id} gig={gig} name={gig.name} resource="audiences" />
        ))}
      </React.Fragment>
    );
  }
}
