import React, { Component } from 'react';
import './Venues.css'

class VenuesCard extends Component {
  render() {
    return (
      <div className="card">
          <h3 className="venuesCard">{this.props.venue.name}</h3> {this.props.venue.city} {this.props.venue.state} {this.props.venue.capacity}
          <picture>
            <img className="capLogo"src={require('./capstoneLogo2.png')} alt="capstone logo" />
          </picture>
          <button type="button" onClick={() => {this.props.history.push(`/venues/${this.props.venue.id}/edit`)}}>Edit</button>
          <button type="button" onClick={() => this.props.deleteVenue(this.props.venue.id)}>Delete Venue</button>
          <button type="button" className="delete--btn" onClick={() => {if(window.confirm('You sure, tho?'))this.props.deleteVebnuye(this.props.venue.id)}}>Delete Venue</button>
          </div>
    );
  }
}

export default VenuesCard;
